sys_variable_value.value:
(function execute(inputs, outputs) {
  var prefUtil = new x_snc_enable_ne.PrefMgmtUtil();
  var fnOutput = prefUtil.savePreference(inputs.user.sys_id, prefUtil.USER_PREF_NAME, inputs.toggleVal);
  outputs.user_id = fnOutput.userId;
  outputs.message = fnOutput.message;
  outputs.user_preference_value = fnOutput.preferenceValue;
  outputs.preference_record_id = fnOutput.preferenceRecordId;

})(inputs, outputs);

