sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var userId = current.getValue("opened_by");
    var prefGR = new GlideRecord("sys_user_preference");
    prefGR.addQuery("name", "glide.ui.polaris.use");
    prefGR.addQuery("user", userId);
    prefGR.query();

    if (prefGR.next()) {
		template.print("enabled");
	} else {
		template.print("disabled");
	}

})(current, template, email, email_action, event);
